package com.acmvit.androidchallenge1.Activities;

import android.os.Bundle;

import androidx.appcompat.app.AppCompatActivity;
import androidx.recyclerview.widget.RecyclerView;

import com.acmvit.androidchallenge1.Adapters.DhaneshAdapter;
import com.acmvit.androidchallenge1.CustomClasses.Place;
import com.acmvit.androidchallenge1.R;

import java.util.ArrayList;

public class DhaneshActivity extends AppCompatActivity {
    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_dhanesh);
        ArrayList<Place> places = new ArrayList<>();
        places.add(new Place("Vellore Fort", " Balaji Nagar, Vellore, Tamil Nadu 632004", "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTMdmnFtvboSk6QxGRLL93I3p3sHpiGI-F4QeJZXFjyypP1td7sKg&s", "Vellore Fort is a large 16th-century fort situated in heart of the Vellore city, in the state of Tamil Nadu, India built by Vijayanagara kings. The fort was at one time the headquarters of the Aravidu Dynasty of the Vijayanagara Empire. The fort is known for its grand ramparts, wide moat and robust masonry. The fort's ownership passed from Vijayanagara kings, to the Bijapur sultans, to the Marathas, to the Carnatic Nawabs and finally to the British, who held the fort until India gained independence. The Indian government maintains the fort with the Archaeological Department. During British rule, the Tipu Sultan's family and the last king of Sri Lanka, Sri Vikrama Rajasinha were held as prisoners in the fort. Vellore fort is one of the most popular tourist destinations for both domestic and international visitors to Tamil Nadu. ", "geo:12.9568774,79.1135513?z=16"));
        places.add(new Place("Golden Temple", "Sri Narayani Peedam, Thirumalaikodi, Vellore, Tamil Nadu 632055", "https://lh4.googleusercontent.com/-35bBdfLI8rw/XhhmyS7mAfI/AAAAAAAAFZ0/jn-1W2x8xXcP375C4VP_tMI-Ogh3eMOjgCLIBGAYYCw/w370-h253-n-k-no/", "Golden Temple Vellore complex inside the Sripuram spiritual park is situated at the foot of a small range of green hills at Thirumalaikodi Vellore in Tamil Nadu, India. It is 120 km from Tirupati, 145 km from Chennai, 160 km from Puducherry and 200 km from Bengaluru. The Maha Kumbhabhishekam or consecration of the temple and its chief deity, Sri Lakshmi Narayani or Maha Lakshmi, the goddess of wealth, was held on 24 August 2007, and devotees from all religions and backgrounds are welcome to visit. This temple is gilded with 1,500 kg of pure gold, double the 750 kg gilding of the dome of the Golden Temple at Amritsar.", "geo:12.873523,79.0860283?z=16"));
        places.add(new Place("Palamathi Hills", "Palamathi R.F, Tamil Nadu 632001", "https://lh5.googleusercontent.com/-4dl62ZODGxw/XgbsuY2ea8I/AAAAAAAEBfc/4rv-nh_Wnmw__pzJgfpb0JZG0g_v0fZNwCLIBGAYYCw/w370-h253-n-k-no/", "The Palamathi Hills are an extension of the Eastern Ghats spread across southeastern parts of Vellore City in Tamil Nadu. The Palamathi Hill range, the nearby Palamathi Reserve Forest, the Otteri lake is collectively referred to as Palamathi Hills. The area is a thriving hotspot for various birds and various flora. The hill top has a famous Hindu temple. The place is also a developing tourist destination.", "geo:12.8849905,79.159143?z=16"));
        places.add(new Place("St. John's Church", "Vellore Fort, Fort Round Rd, Balaji Nagar, Vellore, Tamil Nadu 632004", "https://lh6.googleusercontent.com/-VNdaVVm78aM/Xh_dk5vaa9I/AAAAAAAADiA/e8se-qoA1Xs5hH6FGueqHw3k-T3u2n7kACLIBGAYYCw/w370-h253-n-k-no/", "St. John’s Church is located inside the Vellore Fort, Tamil Nadu State, India. The Church was raised in 1846 by the Government of Madras for the officers and men of the East India Company military station. The church is called after St. John the Evangelist. However, the church was never officially consecrated and hence not officially named as St. John’s Church. St. John's Church is the oldest standing church in the Vellore Diocese. The church stands inside the Vellore Fort, which is under the control of the Archaeological Survey of India, along with the Vijaynagar period Jalakanteswarar Temple, Tippu Mahal, Hyder Mahal, Candy Mahal, Badhusha Mahal and the Begum Mahal.", "geo:12.9201507,79.1258207?z=16"));
        places.add(new Place("Christian Medical College & Hospital", "IDA Scudder Rd, Vellore, Tamil Nadu 632004", "data:image/jpeg;base64,", " Christian Medical College Vellore (CMC Vellore)[5] is a private, minority-run educational and research institute[6][7] that includes a network of primary, secondary and tertiary care[8] hospitals in and around Vellore, Tamil Nadu, India.The institute, constituent college is affiliated with the Tamil Nadu Dr MGR Medical University [9]. Founded in 1900 by an American missionary, Dr Ida S. Scudder, CMC Vellore has brought many significant achievements to India, including starting the first College of Nursing in 1946, performing the first reconstructive surgery for leprosy in the world (1948), performing the first successful open heart surgery in India (1961), performing the first kidney transplant in India (1971), performing first bone marrow transplantation (1986) in India and performing the first successful ABO incompatible kidney transplant in India (2009).", "geo:12.9246021,79.1326242?z=16"));
        places.add(new Place("Vellore Institute Of Technology", "Gorbachev Rd, Vellore, Tamil Nadu 632014", "data:image/jpeg;base64,", "Vellore Institute of Technology is a private higher education institute deemed to be university, located in Vellore, Tamil Nadu, India. Founded in 1984, as Vellore Engineering College, by G. Viswanathan, the institution offers 20 undergraduate, 34 postgraduate, four integrated and four research programs.", "geo:12.9720643,79.1576297?z=15"));
        RecyclerView v = findViewById(R.id.recycler);
        DhaneshAdapter itemAdapter = new DhaneshAdapter(DhaneshActivity.this, places);
        v.setAdapter(itemAdapter);
    }
}
